import React, { useState, useEffect } from 'react';

// Function to dynamically import SVGs
const importSvg = async (iconPath) => {
  try {
    const icon = await import(`${iconPath}`);
    return icon.default;
  } catch (error) {
    console.error(`Error importing ${iconPath}: `, error);
    return null;
  }
};

const ServiceArea = ({ data }) => {
  const [icons, setIcons] = useState({});

  useEffect(() => {
    const loadIcons = async () => {
      const iconPromises = data.map((item) =>
        item.icon ? importSvg(item.icon).then((icon) => ({ [item.icon]: icon })) : Promise.resolve({})
      );
      const iconsArray = await Promise.all(iconPromises);
      const iconsObject = iconsArray.reduce((acc, icon) => ({ ...acc, ...icon }), {});
      setIcons(iconsObject);
    };

    loadIcons();
  }, [data]);

  return (
    <div className="rn-service-area pt-20">
      <div className="container mx-auto">
        <div className="section-title text-left px-28">
          <span className="subtitle">Features</span>
          <h2 className="title">What I Do</h2>
        </div>
        <div className="flex flex-wrap items-center px-24">
          {data.map((item, index) => (
            <div className="w-1/3 px-5 mt-10" key={index}>
              <div className="rn-service text-left">
                <div className="inner">
                  <div className="icon">
                    {icons[item.icon] && <img src={icons[item.icon]} alt={item.title} />}
                  </div>
                  <div className="content">
                    <h4 className="title">
                      <a href="#">{item.title}</a>
                    </h4>
                    <p className="description">{item.description}</p>
                    <a className="read-more-button" href="#">
                      <i className="feather-arrow-right"></i>
                    </a>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ServiceArea;
