import { useRef } from "react";
import Slider from "react-slick";
import Headings from "./Headings";
import Modal from './Modal';


export default ({projectdata}) => {
    const modal = useRef();
    function handleSave() {
        modal.current.open();
    }

    var settings = {
        dots: false,
        infinite: false,
        speed: 500,
        arrows: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        initialSlide: 0,
        adaptiveHeight: true,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 1,
              initialSlide: 2
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
    };
    return (
        <>
        <div className="project-carousel pb-20">
            <div className="container mx-auto">
                <Headings title="Recent Projects" subtitle='my work' />
                <div className="px-24 mt-10">
                    <div className="slider-container">
                        <Slider {...settings}>
                            {projectdata.map((item, index) => (
                                <div className="px-4" key={index}>
                                    <div className="project-portfolio mb-5 p-7 text-left relative">
                                        <div className="inner-wrapper">
                                            <div className="thumbnail-image">
                                                <a href="javascript:void(0)" className="block rounded-lg overflow-hidden" onClick={handleSave}>
                                                    <img className="object-cover w-full h-96" src={item.image} alt="Personal Portfolio Images" />
                                                </a>
                                            </div>
                                            <div className="content-info pt-4">
                                                <div className="category-info pb-2.5 flex justify-between">
                                                    <div className="category-list flex flex-wrap items-center">
                                                        <a href="javascript:void(0)" className="uppercase block text-xs font-medium">{item.tag}</a>
                                                    </div>
                                                    <div className="meta">
                                                        <span className="font-medium text-xs">
                                                            <a href="javascript:void(0)" tabindex="-1"><i className="feather-heart"></i></a>
                                                            {item.likes}
                                                        </span>
                                                    </div>
                                                </div>
                                                <h4 className="title">
                                                    <a href="javascript:void(0)" className="font-semibold text-2xl">{item.title}<i class="feather-arrow-up-right"></i></a>
                                                </h4>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </Slider>
                    </div>
                </div>
            </div>
        </div>
        <Modal ref={modal} buttonCaption="Okay">
            <h2 className='text-xl font-bold text-stone-700 my-4'>Invalid Input</h2>
            <p className='text-stone-600 mb-4'>Oops ... looks like you forget to enter a value.</p>
            <p className='text-stone-600 mb-4'>Please make sure you provide a valid value for every input field.</p>
        </Modal>
        </>
      );
};
